export default {
  name: '',
  components: {},
  props: [''],

  data() {
    return {
      ruleForm: {
        name: null,
        phone: null,
        code: null,
        age: null,
        sex: 0
      },
      CountDown: 60,
      //倒计时
      buttonName: "发送验证码",
      //按钮初始化文字
      isDisabled: false,
      //是否
      formLabelWidth: '80px',
      rules: {
        name: [{
          required: true,
          message: '请输入姓名',
          trigger: 'blur'
        }],
        phone: [{
          required: true,
          message: '请输入手机号',
          trigger: 'blur'
        }, {
          min: 11,
          max: 11,
          message: '请输入正确的手机号码',
          trigger: 'blur'
        }],
        code: [{
          required: true,
          message: '请输入验证码',
          trigger: 'blur'
        }],
        age: [{
          required: true,
          message: '请输入年龄',
          trigger: 'blur'
        }],
        sex: [{
          required: true,
          message: '请选择性别',
          trigger: 'blur'
        }]
      }
    };
  },

  watch: {},

  created() {},

  beforeMount() {},

  mounted() {},

  methods: {
    //点击获取验证码倒计时
    getCode() {
      this.isDisabled = true;
      let timerId = setInterval(() => {
        this.CountDown--;
        this.buttonName = this.CountDown + "秒后重试";

        if (this.CountDown <= 0) {
          clearInterval(timerId);
          this.buttonName = "重新发送";
          this.CountDown = 60;
          this.isDisabled = false;
        }
      }, 1000);
    },

    // 提交
    submitForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          this.$message({
            message: '数据提交成功',
            type: 'success'
          });
          console.log(this.ruleForm); // 提交的数据，发起网络请求

          this.cancel();
        } else {
          console.log('error submit!!');
          return false;
        }
      });
    },

    // 取消
    cancel() {
      this.$emit("closeDialog");
    }

  },
  computed: {}
};
