import { Header, Footer, SubmitDialog } from "./components/index.js";
export default {
  name: "App",
  components: {
    Header: Header,
    Footer: Footer,
    SubmitDialog: SubmitDialog
  },
  data: function data() {
    return {
      isShow: false,
      dialogFormVisible: false
    };
  },
  mounted: function mounted() {
    window.addEventListener('scroll', this.handleScroll, true);
    this.autoScroll();
  },
  methods: {
    // 弹窗阻止页面滚动
    stopScroll: function stopScroll() {
      document.body.style.top = '0'; // document.body.style.position = 'fixed'

      document.body.style.height = '100%'; // document.body.style.overflow = 'hidden'
    },
    // 回复页面滚动
    autoScroll: function autoScroll() {
      document.body.style.position = 'initial';
      document.body.style.overflowY = 'auto';
    },
    toFreeToast: function toFreeToast() {
      this.dialogFormVisible = true;
      this.stopScroll();
    },
    handleScroll: function handleScroll() {
      var scrollTop = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop;

      if (scrollTop > 100) {
        this.isShow = true; // 显示
      } else {
        this.isShow = false; // 隐藏
      }
    },
    toTop: function toTop() {
      var top = document.documentElement.scrollTop || document.body.scrollTop; // 实现滚动效果

      var timeTop = setInterval(function () {
        document.body.scrollTop = document.documentElement.scrollTop = top -= 100; //可以自己设置

        if (top <= 0) {
          clearInterval(timeTop);
        }
      }, 10);
    },
    //关闭弹窗
    closeDialog: function closeDialog() {
      this.dialogFormVisible = false;
      this.autoScroll();
    }
  }
};
